<?php session_start(); require_once __DIR__.'/../config/database.php'; if(!isset($_SESSION['user'])) header('Location: login.php'); $msg=$_GET['msg']??''; $res=$conn->query("SELECT * FROM galeri ORDER BY created_at DESC"); ?><!doctype html><html><head><meta charset="utf-8"><title>Galeri Admin</title></head><body><h2>Galeri</h2><?php if($msg==='success') echo '<p style="color:green">Upload sukses</p>'; ?><form action="galeri_save.php" method="post" enctype="multipart/form-data"><input name="judul" placeholder="Judul"><br><textarea name="deskripsi" placeholder="Deskripsi"></textarea><br><input type="date" name="tanggal"><br><input type="file" name="foto" accept="image/*"><br><button>Upload</button></form><hr><?php while($r=$res->fetch_assoc()){ echo '<div><img src="'.htmlspecialchars($r['file']).'" height="80"><br><strong>'.htmlspecialchars($r['judul']).'</strong> <a href="galeri_delete.php?id='.$r['id'].'">Hapus</a></div>'; } ?></body></html>