<?php header('Content-Type: application/json'); require_once __DIR__.'/../config/database.php'; $today=date('Y-m-d'); $q=$conn->query("SELECT SUM(IF(jenis='masuk',jumlah,0)) as masuk, SUM(IF(jenis='keluar',jumlah,0)) as keluar FROM kas WHERE DATE(tanggal)='$today'")->fetch_assoc(); $m=$q['masuk']?:0; $k=$q['keluar']?:0; $s=$m-$k; function fmt($v){return 'Rp '.number_format($v,0,',','.');} echo json_encode(['masuk'=>$m,'keluar'=>$k,'saldo'=>$s,'masuk_formatted'=>fmt($m),'keluar_formatted'=>fmt($k),'saldo_formatted'=>fmt($s)]); ?>